<?php

namespace Runalyze\Model\Activity\Splits;

/**
 * Generated by hand
 */
class CompletorForTimeTest extends \PHPUnit_Framework_TestCase {

	public function testCompletor() {
		$Splits = new Entity(array(
			new Split(1.0, 0),
			new Split(1.3, 0)
		));

		$Completor = new CompletorForTime($Splits,
			array(100, 200, 300, 400, 550, 650),
			array(0.3, 0.6, 1.0, 1.4, 1.9, 2.3)
		);
		$Completor->completeSplits();

		$this->assertEquals(300, $Splits->at(0)->time());
		$this->assertEquals(350, $Splits->at(1)->time());
	}

}
